import React from 'react';
import { fetchPosts } from '../ajax-requests'

const token = window.localStorage.getItem('token');


const Posts = ({posts}) => {



    return (
        <div>
            <h1>Posts</h1>
            <form></form>
            <p>Create a post here:</p>

            <button onClick={() => createPost(token)}>Create Post</button>

            <ul>
                {posts?.map((post => {
                    console.log(post);
                    return (
                        <ul className="cards">
                            <li
                                key={post._id}
                                className={post.isAuthor ? "myPost" : "singlePost"}
                            >
                                <Link to={`/posts/${post.id}`} className="link">
                                    <h3>{post.title}</h3>
                                </Link>
                                <p>{post.description}</p>
                                <p>Price: {post.price}</p>
                                <p>Location: {post.location}</p>
                                <p>Date Posted: {post.updatedAt}</p>
                                <p>Author: {post.author.username}</p>
                                {post.isAuthor ? <button>Edit</button> : null}
                            </li>
                        </ul>
                    );
                }))}
            </ul>
        </div>
    );
};  


const createPost = async (token) => {

    try {
        const response = await fetch(`${`https://strangers-things.herokuapp.com/api/${COHORT_NAME}`}/posts`, {
            method: "POST",
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`
            },
            body: JSON.stringify({
                post: {
                    title: `${postTitle}`,
                    description: `${postDescription}`,
                    price: `${postPrice}`,
                    willDeliver: true
                }
            })
        });
        const result = await response.json();
        console.log(result);
        return result
    } catch (err) {
        console.error(err);
    }
}

export default Posts;
