import React, { useState, useEffect } from 'react';
import { Routes, Route, Link, useNavigate } from 'react-router-dom';
import { 
    Register,
    Posts,
    Login,
    CreatePost, 
    Profile,
    Home,
    Message,
    Search,
    EditPost
 } from './';
import { fetchPosts, myData } from '../ajax-requests'



export function App() {
    const [token, setToken] = useState('');
    const [posts, setPosts] = useState([]);
    const [user, setUser] = useState({});
    const [isLoggedIn, setIsLoggedIn]= useState(false);

    const navigate= useNavigate();


    function tokenCheck() {
        if (window.localStorage.getItem('token')) {
            setToken(window.localStorage.getItem('token'));

        }
    }

  async  function getPosts () {
        const results =await fetchPosts();
        if (results.success) {
            setPosts(results.data.posts)
        }
    }


    async function myData() {
        const results = await myData(token);
        if (results.success) {
            setUser(results.data);
        }
    }

    useEffect(() => {
        tokenCheck();
    }, [])

    useEffect (() => {
        getPosts();
        if (token){
            myData();
            setIsLoggedIn(true);
        }
    }, [token])


    const logout = () => {
        localStorage.clear();

    }

    return (
        <div>

            <Nav setToken={setToken}
            setIsLoggedIn={setIsLoggedIn}
            isLoggedIn={isLoggedIn}
            />
                <h1 className='title'>Stranger's Things</h1>
            <ul>
                <li><Link to='/home'>Home</Link></li>
                <li> <Link to='/posts'>Posts </Link></li>
                <li><Link to='/profile'>Profile</Link></li> 
                <li><button onClick={logout}>Log Out</button></li>
                </ul>

                {/* fix!!!!!!!!!! */}

            <Routes>
             <Route
             path='/'
             element= {<Login/>}
             />
                <Route
                    path='/posts'
                    element={<Posts posts={posts} />}
                />
             <Route
                    path='/register'
                    element = {<Register setToken={setToken} />} 
             />
            <Route
                    path='/login'
                    element ={<Login setToken={setToken} /> }
            />
            <Route
                    path= '/create-post'
                    element={<CreatePost token={token} getPosts={getPosts} />}
            />
                <Route
                    path='/profile'
                    element={<Profile token={token} posts={posts} />}
                />
                <Route
                    path='/home'
                    element={<Home />}
                />
                <Route
                    path='/message'
                    element={<Message />}
                />
                <Route
                    path='/search'
                    element={<Search />}
                />
                <Route
                    path='/edit-post/:postId'
                    element={<EditPost posts={posts} token={token} />}
                />
            </Routes>
        </div>
    )
}

export default App;
