import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom'
import { fetchPosts } from '../ajax-requests'
import { handleMessage } from './Message'


function Posts ({ posts, token, handleMessage }) {
   const [searching, setSearching] = useState('');

   useEffect(() => {
   fetchPosts()
 }, [])


  const searchPosts = (posts) => {
     for (const element of posts) {
        return (posts.title)
    }
 }

    return (
        <>
            {token === null ? '' : <Link to='/create-post'>Add Post</Link>}
            <input
            className='searchBar'
            type='text'
            placeholder='Search Posts'
            value={searching}
            onChange={(event) => {
                setSearching(event.target.value)
                searchPosts()

            }}
            ></input>




            <div className='postBody'>
                {posts && posts.map((post) => {
                    return (
                        <div
                        key={post._id}
                        className= {post.isAuthor ? "myPost" : 'singlePost'}>
                            <h3>{post.title}</h3>
                            <p>{post.description}</p>
                            <p>Price: {post.price}</p>
                            <p>Date Posted: {post.updatedAt}</p>
                            <p>Author: {post.author.username}</p>


                            {post.isAuthor ? ( 
                                <>
                             <Link to={`/update-post/${post._id}`} ><button>Edit</button></Link>
                            <button>Delete</button>

                                </>

                           ) : (

                           <div>
                            {token === null ? '' : <div>
                                {post.isAuthor === false ? <Link to="/Message">
                                    <button className="messageButton" onClick={() => handleMessage(post._id.data)}>Message</button>
                                </Link> : ''
                                }

                            </div>

                            }

                       </div> 


                    )}

                    </div> 

                 ) })

                }   

            </div>
        </>

                    );

}





export default Posts;
