import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom'
import { fetchPosts } from '../ajax-requests'



function Posts({ posts, token }) {
   const [searching, setSearching] = useState('');

   useEffect(() => {
   fetchPosts()
 }, [])

 const searchPosts = () => {
    for (const element of posts) {
        return (posts.title)
    }
 }

    return (
        <>
            {token === null ? '' : <Link to='/create-post'>Add Post</Link>}
            <input
            className='searchBar'
            type='text'
            placeholder='Search Posts'
            value={searching}
            onChange={(event) => {
                setSearching(event.target.value)
                searchPosts()
                console.log(searching)
            }}
            ></input>




                {posts && posts.map((post) => {
                    return (
                        <div
                        key={post._id}
                        className= {post.isAuthor ? "myPost" : 'singlePost'}>
                            <h3>{post.title}</h3>
                            <p>{post.description}</p>
                            <p>Price: {post.price}</p>
                            <p>Date Posted: {post.updatedAt}</p>
                            <p>Author: {post.author.username}</p>
                            {post.isAuthor ? <button>Edit</button> :null}
                            {post.isAuthor ? <button>Delete</button> :null}
                       </div> 
                    )
                })
            }
        </>
    );
}





export default Posts;
