import React, { useState } from 'react'
import { BASE_URL } from '../ajax-requests';
import { useNavigate, useParams } from 'react-router-dom'
import { postMessage } from '../ajax-requests';


const Message = ({ token }) => {
    const [message, setMessage] = useState('')


    const handleMessage = async (event, message, postId) => {
        event.preventDefault();
        postMessage()
        // try {
        //     const res = await fetch(`${BASE_URL}/posts/${postId}/messages`, {
        //         method: "POST",
        //         headers: {
        //             'Content-Type': 'application/json',
        //             'Authorization': `Bearer ${token}`
        //         },
        //         body: JSON.stringify({
        //             message: {
        //                 content: `${message}`
        //             }
        //         })
        //     });
        //     const data = await res.json();
        //     console.log(data)
            setMessage('');
            alert("Message Sent!")
            navigate("/Posts");

    }




    const handleChange = async (event) => {
        await setMessage(event.target.value);
    }



    return (
        <div className="sendAMessage">
            <form onSubmit={handleMessage}>
                <div className="messageCreate">
                    <label className="message" htmlFor="message">Message:</label>
                    <input className="messageContent"
                        type="text"
                        name="message"
                        value={message}
                        onChange={handleChange}
                    />
                </div>
                <button className="sendMessageButton" type="submit">Send Message!</button>

            </form>
        </div>
    )
};


;

export default Message;
