            )}
            <input
                type="text"
                className="form-control my-4"
                placeholder="Search for a post"
                value={searchValue}
                onChange={(event) => setSearchValue(event.target.value)}
            />

            {filteredPosts?.map((post) => (
                <Post key={post._id} post={post} token={token} setPosts={setPosts} />
            ))}
        </div>
    );
