import React, { useState, useEffect } from 'react';
import { Routes, Route, Link } from 'react-router-dom';
import { 
    Register,
    Posts,
    Login,
    CreatePost
 } from './';
import { fetchPosts } from '../ajax-requests'



function App() {
    const [token, setToken] = useState('');
    const [posts, setPosts] = useState([]);
    const [userData, setUserData] = useState ({});

    function tokenCheck() {
        if (window.localStorage.getItem('token')) {
            setToken(window.localStorage.getItem('token'));
        }
    }

  async  function getPosts () {
        const results =await fetchPosts();
        if (results.success) {
            setPosts(results.data.posts)
        }
    }



    useEffect(() => {
        tokenCheck();
    }, [])

    useEffect (() => {
        getPosts();
    }, [token])


    const logout = () => {
        localStorage.clear();

    }

    return (
        <div>

            <nav>
                <h1 className='title'>Stranger Things</h1>
            <ul>
                <li> <Link to='/posts'>Posts ({posts.length})</Link></li>
                <li><Link to='/login'>Login</Link></li>
                <li> <Link to='/create-post'>New Post</Link></li>
                <li> <button onClick={logout}>Log Out</button></li>
                </ul>
            </nav>
            <Routes>
             <Route
             path='/'
             element= {<Login/>}
             />
                <Route
                    path='/posts'
                    element={<Posts posts={posts} />}
                />
             <Route
                    path='/register'
                    element = {<Register setToken={setToken} />} 
             />
            <Route
                    path='/login'
                    element ={<Login setToken={setToken} /> }
            />
            <Route
                    path= '/create-post'
                    element={<CreatePost token={token} getPosts={getPosts} />}
            />
            </Routes>
        </div>
    )
}

export default App;
